import { SET_THEME } from './contants';

const initState = {
    todos: [],
    todoinput: '',
};

function reducer(state, action) {
    switch (action.type) {
        case SET_THEME:
            return {
                ...state,
                todoInput: action.payload === 'dark' ? 'light' : 'dark',
            };
        default:
            throw new Error('Invalid action.');
    }
}

export { initState };
export default reducer;
