import styles from './Audio.module.scss';
import className from 'classnames/bind';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePause, faCirclePlay, faVolumeHigh } from '@fortawesome/free-solid-svg-icons';
import { useState, useRef } from 'react';
import { sounds } from '../../Sounds';

const cx = className.bind(styles);

function Audio() {
    const [play, setPlay] = useState(false);
    const oceanRef = useRef();
    const MAX = 20;

    const PlayPause = () => {
        setPlay(!play);
    };

    function toggleAudio() {
        if (play) {
            oceanRef.current.pause();
        } else {
            oceanRef.current.play();
        }
    }
    function handleVolume(e) {
        const { value } = e.target;
        const volume = Number(value) / MAX;
        oceanRef.current.volume = volume;
    }

    return (
        <div className={cx('audio')}>
            <button className={cx('play')} onClick={toggleAudio}>
                {!play ? (
                    <FontAwesomeIcon icon={faCirclePlay} onClick={PlayPause} />
                ) : (
                    <FontAwesomeIcon icon={faCirclePause} onClick={PlayPause} />
                )}
            </button>
            <button className={cx('volume')}>
                <input type="range" min={0} max={20} onChange={(e) => handleVolume(e)} />
                <FontAwesomeIcon icon={faVolumeHigh} />
            </button>
            <audio ref={oceanRef} loop src={sounds.link} />
        </div>
    );
}

export default Audio;
