import styles from './SoundMenu.module.scss';
import className from 'classnames/bind';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVolumeHigh, faVolumeMute } from '@fortawesome/free-solid-svg-icons';

import Button from '../Button';
import { sounds } from '../../Sounds';
import { useState } from 'react';
import { useRef } from 'react';
import React from 'react';

const cx = className.bind(styles);

function SoundMenu() {
    var settings = {
        dots: true,
        className: `${styles.slickslider}`,
        dotsClass: `slick-dots ${styles.dots}`,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        initialSlide: 0,
        autoplay: false,
        autoplaySpeed: 2000,
    };

    const [turnon, setTurnOn] = useState(false);
    const soundRef = useRef([...new Array(sounds.length)].map(() => React.createRef()));

    console.log(soundRef);

    const toggleSound = () => {
        if (turnon) {
            soundRef.current.pause();
            setTurnOn(false);
        } else {
            soundRef.current.play();
            setTurnOn(true);
        }
    };

    useEffect(() => {
        soundRef.current[0].current.focus();
    }, []);

    return (
        <div className={cx('slick')}>
            <Slider {...settings}>
                {sounds.map((item, index) => (
                    <div className={cx('wrapper')} key={index}>
                        <div className={cx('sound-item')}>
                            <Button icon>
                                <img src={item.icon} alt={item.name} />
                                <span>{item.name}</span>
                            </Button>
                            <span className={cx('suggest')}>{item.suggest}</span>
                        </div>
                        <button className={cx('volume-item')} onClick={toggleSound}>
                            {!turnon ? (
                                <FontAwesomeIcon icon={faVolumeMute} />
                            ) : (
                                <FontAwesomeIcon icon={faVolumeHigh} />
                            )}
                        </button>
                        <audio src={item.link} ref={soundRef.current[index]} loop></audio>
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default SoundMenu;
