import { SET_THEME } from './contants';

const initState = {
    todos: 'dark-mode',
};

function reducer(state, action) {
    switch (action.type) {
        case SET_THEME:
            return !state;
        default:
            throw new Error('Invalid action.');
    }
}

export { initState };
export default reducer;
