import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloudShowersHeavy, faCloudSun } from '@fortawesome/free-solid-svg-icons';

import className from 'classnames/bind';
import styles from './DarkMode.module.scss';
import { useStore } from '../../store';

const cx = className.bind(styles);

const DarkMode = () => {
    // const setDarkMode = () => {
    //     document.querySelector('body').setAttribute('data-theme', 'dark');
    // };

    // const setLightMode = () => {
    //     document.querySelector('body').setAttribute('data-theme', 'light');
    // };

    // const toggleTheme = (e) => {
    //     if (e.target.checked) setDarkMode();
    //     else setLightMode();
    // };

    const [state, dispatch] = useStore;
    const { darkMode } = state;

    const toggleTheme = () => {
        if (darkMode) dispatch({ type: 'LIGHTMODE' });
        else dispatch({ type: 'DARKMODE' });
    };

    return (
        <div className={cx('dark-mode')}>
            <input className={cx('dark_mode_input')} type="checkbox" id="darkmode-toggle" onChange={toggleTheme} />
            <label className={cx('dark_mode_label')} htmlFor="darkmode-toggle">
                <FontAwesomeIcon className={cx('sunny')} icon={faCloudSun} />
                <FontAwesomeIcon className={cx('rainy')} icon={faCloudShowersHeavy} />
            </label>
        </div>
    );
};

export default DarkMode;
