import styles from './SoundMenu.module.scss';
import className from 'classnames/bind';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePause, faCirclePlay, faVolumeHigh, faVolumeMute } from '@fortawesome/free-solid-svg-icons';

import Button from '../Button';
import { sounds } from '../../Sounds';
import { useState } from 'react';
import { useRef } from 'react';
import { useEffect } from 'react';
import React from 'react';

const cx = className.bind(styles);

function SoundMenu() {
    var settings = {
        dots: true,
        className: `${styles.slickslider}`,
        dotsClass: `slick-dots ${styles.dots}`,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        initialSlide: 0,
        autoplay: false,
        autoplaySpeed: 2000,
    };

    const [turnon, setTurnOn] = useState(false);
    const [length, setLength] = useState(2);
    const soundRef = useRef([...new Array(3)].map(() => React.createRef()));

    const toggleSound = () => {
        if (turnon) {
            soundRef.current.pause();
            setTurnOn(false);
        } else {
            soundRef.current.play();
            setTurnOn(true);
        }
    };

    function updateLength({ target: { value } }) {
        setLength(value);
        soundRef.current = soundRef.current.splice(0, value);
        for (let i = 0; i < value; i++) {
            soundRef.current[i] = soundRef.current[i] || React.createRef();
        }
        soundRef.current = soundRef.current.map((item) => item || React.createRef());
    }

    useEffect(() => {
        soundRef.current[soundRef.current.length - 1].current.focus();
    }, [length]);

    return (
        <div className={cx('slick')}>
            <Slider {...settings}>
                {sounds.map((item, index) => (
                    <div className={cx('wrapper')} key={index}>
                        <div className={cx('sound-item')}>
                            <Button icon>
                                <img src={item.icon} alt={item.name} />
                                <span>{item.name}</span>
                            </Button>
                            <span className={cx('suggest')}>{item.suggest}</span>
                        </div>
                        <button className={cx('volume-item')} onClick={toggleSound}>
                            {!turnon ? (
                                <FontAwesomeIcon icon={faVolumeMute} />
                            ) : (
                                <FontAwesomeIcon icon={faVolumeHigh} />
                            )}
                            <audio src={item.link} ref={soundRef.current[i]} loop></audio>
                        </button>
                    </div>
                ))}
            </Slider>
            <input value={soundRef.current.length} type="number" onChange={updateLength} />
        </div>
    );
}

export default SoundMenu;
