import styles from './SoundMenu.module.scss';
import className from 'classnames/bind';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePause, faCirclePlay, faVolumeHigh } from '@fortawesome/free-solid-svg-icons';

import Button from '../Button';
import { sounds } from '../../Sounds';

const cx = className.bind(styles);

function SoundMenu() {
    var settings = {
        dots: true,
        className: `${styles.slickslider}`,
        dotsClass: `slick-dots ${styles.dots}`,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        initialSlide: 0,
        autoplay: true,
        autoplaySpeed: 2000,
    };

    return (
        <div className={cx('slick')}>
            <Slider {...settings}>
                {sounds.map((item, index) => (
                    <div className={cx('sound-item')} key={index}>
                        <Button icon>
                            <img src={item.icon} alt={item.name} />
                            <span>{item.name}</span>
                            <button className={cx('volume-item')}>
                                <FontAwesomeIcon icon={faVolumeHigh} />
                                <audio src={item.link}></audio>
                            </button>
                        </Button>
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default SoundMenu;
